import React, { useState } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../../styles/SetBudget.css';
import config from '../../config';
import Alert from 'react-s-alert';
import 'react-s-alert/dist/s-alert-default.css';
import 'react-s-alert/dist/s-alert-css-effects/slide.css';

const SetBudget = ({ token }) => {
  const [category, setCategory] = useState('');
  const [budgetAmount, setBudgetAmount] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleAddBudget = async () => {
    try {
      const response = await axios.post(
        `${config.apiUrl}/api/budgets`,
        { budgetName: category, budgetNumber: budgetAmount, selectedDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      Alert.success('Budget added successfully', {
        position: 'top-right',
        effect: 'slide',
        timeout: 3000,
      });

      setCategory('');
      setBudgetAmount('');
      setSelectedDate(new Date());
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'An error occurred';
      Alert.error(`Error: ${errorMessage}`, {
        position: 'top-right',
        effect: 'slide',
        timeout: 3500,
      });
    }
  };

  // Function to handle input changes
  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleBudgetAmountChange = (e) => {
    setBudgetAmount(e.target.value);
  };

  return (
    <div className="create-budget-container">
      <h2>Add a New Budget</h2>
      <div className="input-group">
        <label htmlFor="category"></label>
        <input
          type="text"
          id="category"
          value={category}
          onChange={handleCategoryChange}
          placeholder="Category" // Use placeholder attribute
        />
      </div>
      <div className="input-group">
        <label htmlFor="budgetAmount"></label>
        <input
          type="number"
          id="budgetAmount"
          value={budgetAmount}
          onChange={handleBudgetAmountChange}
          placeholder="Amount" // Use placeholder attribute
        />
      </div>
      <button className="create-button" onClick={handleAddBudget}>
        Add Budget
      </button>
      <Alert stack={{ limit: 3 }} />
    </div>
  );
};

export default SetBudget;
